params = $.deparam.querystring();

function updateVideoFeed() {
    url = ${ "\"/feed/_all\"" if feed.all else "\"/feed/%s/%s/\"" %(feed.owner.username, feed.name)}
    $.get( url, function( xml ) {
      xmlDoc = $.parseXML( xml );
      xml = $( xmlDoc );
      vf = $( "#videoFrame" );
      if ('stream' in params) {
          page = "${static('flashls.html')}?url=" + encodeURIComponent(xml.find("streamURL").text());
          console.log(page);
          if (vf.attr("src") != page) {
              console.log("New video URL " + page);
              vf.attr("src", page);
          }
      }
      else {
          url = xml.find("url").text();
          if (vf.attr("src") != url) {
              console.log("New video URL " + url);
              vf.attr("src", url);
          }
      }
    })
    .always(function() {
      setTimeout(updateVideoFeed, 5000);
    });
}
updateVideoFeed();

function resizeWindow() {
    vf = $( "#videoFrame" );
    vf.attr("width", $(document).width());
    vf.attr("height", $(document).height());
}
resizeWindow();
$( window ).resize(resizeWindow);
